using System;
using AM.Reflection;

class Canary
{
    public int myProp;
    private int MyProp
    {
        get
        {
            return myProp;
        }
        set
        {
            myProp = value;
        }
    }
}

class Program
{
    static void Main ( string [] args )
    {
        Canary canary = new Canary ();
        PropertyAccessor<Canary, int> pa
           = new PropertyAccessor<Canary, int> ( canary, "MyProp" );
        pa.Value = 2;
        Console.WriteLine ( canary.myProp );
    }
}
